import React from 'react';
import {
    Dropdown
} from 'semantic-ui-react';

export default function CorporateClients({handleOnCorporateClientSearch, handleOnCorporateClientChange, corporateClients: data, selected, multiple=false }) {


    let allClients = []
    data.forEach(client => {

        allClients.push({
            key: client.id,
            text: client.name+ " [" + client.email + "]",
            value: client.id
        })
    })
    return (
        <div>
            {allClients && <Dropdown
                placeholder='Select Corporate Client'
                fluid
                search
                clearable
                multiple={multiple}
                selection
                required
                defaultValue={selected}
                onChange={handleOnCorporateClientChange}
                onSearchChange={handleOnCorporateClientSearch}
                options={allClients}
            />}
        </div>

    )
}
