import React, { useState, useContext, useEffect, useCallback } from 'react';
import {
    Grid,
    Container,
    Header,
    Icon,
    Form,
    Label,
    Input,
    Button,
    Divider,
    Message,
    Modal,
    Tab,
    Menu,
    Item,
    Segment
} from 'semantic-ui-react';
import moment from 'moment';
import { DateInput } from 'semantic-ui-calendar-react';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

import { useMutation, useQuery, useLazyQuery } from '@apollo/react-hooks';
import { MotorPolicyContext } from '../../../context/policy/motor';
import { CREATE_MOTOR_POLICY, GET_MOTOR_POLICY_OPTS, FETCH_INSURANCE_COMP } from '../queries'
import Clients from '../listClients'
import TransactionTypes from '../transactionTypes'
import PremiumTypes from '../premiumTypes'
import InsuranceCompanies from '../listInsuranceComps'
import { FETCH_CLIENTS_QUERY } from '../../auth/queries'
import * as yup from "yup";

export default function AddNewMotorPolicy(props) {
    const [errors, setErrors] = useState({
        errorPaths: [],
        errors: []
    });
    const [responseErrors, setResponseErrors] = useState([]);
    const context = useContext(MotorPolicyContext);
    const [clients, setClients] = useState();
    const [selectedClient, setSelectedClient] = useState();
    const [openModal, setOpenModal] = useState(false);
    const [policyOpts, setPolicyOpts] = useState();
    const [insuranceCos, setInsuranceCos] = useState();
    const [addBenefits, setAddBenefits] = useState({});
    const [addBenefitCount, setAddBenefitCount] = useState([0]);
    const [addPremiumCount] = useState([1, 2, 3]);
    const [addPremiums, setAddPremiums] = useState({
        1: {
            premium: "IPHCFLEVY",
            commissionRate: 0.25,
            amount: 0
        },
        2: {
            premium: "TLEVY",
            commissionRate: 0.2,
            amount: 0
        },
        3: {
            premium: "STAMPD",
            minimumAmount: 40
        },
    });
    const [visible, setVisible] = useState(false);
    const [calculated, setCalculated] = useState({
        netPremium: 0,
        grossPremium: 0,
        grossCommission: 0,
        netCommission: 0,
        withholdingTax: 0,
        updated: false
    });
    const [search, setSearch] = useState({ search: "" });
    const [usersFetched, setUsersFetched] = useState(false);
    const [values, setValues] = useState({
        additionalBenefits: {},
        additionalPremiums: {},
        remarks: "",
        afterSubmit: false,
        commissionRate: 3.75,
        withholdingTax: 10,
        transactionDate: moment().format("YYYY-MM-DD"),
        premiumType: "BASIC",
        vehicleDetails: {
            registrationNo: '',
            make: '',
            body: '',
            color: '',
            chasselNo: '',
            cc: '',
            engineNo: '',
            tonnage: '',
            seatingCapacity: '',
            yearOfManufacture: '',
        }
    });

    let schema = yup.object().shape({
        debitNoteNo: yup.string().required(""),
        policyNo: yup.string().required(""),
        individualClient: yup.string().required(""),
        insuranceCompany: yup.string().required(""),
        insuranceClass: yup.string().required(""),
        transactionType: yup.string().required(""),
        transactionDate: yup.date().required(""),
        startDate: yup.date().required(""),
        endDate: yup.date().required(""),
        yearOfManufacture: yup.number().required(""),
        premiumType: yup.string().required(""),
        minimumPremiumAmount: yup.number().required(""),
        commissionRate: yup.number().required(""),
        policyCommissionRate: yup.number().required(""),
        value: yup.number().required(""),
        tonnage: yup.number().required(""),
        seatingCapacity: yup.number().required(""),
        registrationNo: yup.string().required(""),
        make: yup.string().required(""),
        model: yup.string().required(""),
        body: yup.string().required(""),
        color: yup.string().required(""),
        engineNo: yup.string().required(""),
        chasselNo: yup.string().required(""),
        cc: yup.string().required(""),
    });


    const { data: policyOptsData } = useQuery(GET_MOTOR_POLICY_OPTS);
    useEffect(() => {
        if (policyOptsData) {
            setPolicyOpts(policyOptsData.motorPolicyOptions);
        }
    }, [policyOptsData, policyOpts]);

    const { data: insuranceCosData } = useQuery(FETCH_INSURANCE_COMP, {
        variables: { search: search.searchString }
    });
    useEffect(() => {
        if (insuranceCosData) {
            setInsuranceCos(insuranceCosData.insuranceCompanies.items);
        }
    }, [insuranceCosData, insuranceCos]);

    useEffect(() => {
        if (values.minimumPremiumAmount && values.value && calculated.updated) {
            let data = {}
            let netPremium = (+values.commissionRate / 100 * +values.value).toFixed(0)
            if (+values.minimumPremiumAmount > +netPremium) {
                netPremium = parseFloat(values.minimumPremiumAmount).toFixed(0)
            }
            data["netPremium"] = netPremium
            let grossPremium = netPremium
            for (const [key, value] of Object.entries(addBenefits)) {
                if (value.commissionRate && value.minimumAmount) {
                    let amount = parseFloat(grossPremium * value.commissionRate / 100).toFixed(0)
                    if (+value.minimumAmount > +amount) {
                        amount = +value.minimumAmount
                    }
                    grossPremium = +amount + +grossPremium
                    addBenefits[key].amount = +amount
                }
            }
            for (const [key, value] of Object.entries(addPremiums)) {
                if (value.commissionRate) {
                    let amount = parseFloat(grossPremium * value.commissionRate / 100).toFixed(0)
                    grossPremium = +amount + +grossPremium
                    addPremiums[key].amount = amount
                }
                if (!value.commissionRate) {
                    grossPremium = +value.minimumAmount + +grossPremium
                }
            }
            data["grossPremium"] = grossPremium
            if (values.policyCommissionRate) {
                let grossCommission = (grossPremium * parseFloat(values.policyCommissionRate) / 100).toFixed(0)
                let withholdingTax = (grossCommission * 0.1).toFixed(0)
                let netCommission = grossCommission - withholdingTax
                data["grossCommission"] = grossCommission
                data["withholdingTax"] = withholdingTax
                data["netCommission"] = netCommission
            }
            setCalculated({ ...calculated, ...data, updated: false })
        }
    }, [calculated, values, addPremiums, addBenefits])

    const [fetchClients, { data: clientsData }] = useLazyQuery(FETCH_CLIENTS_QUERY, {
        variables: search
    });
    useEffect(() => {
        if (!usersFetched) {
            fetchClients()
        }
        if (clientsData) {
            setClients(clientsData.individualClients.items);
            setUsersFetched(true)
        }
    }, [clientsData, clients, fetchClients, usersFetched]);
    const validate = useCallback((values) => {
        schema.validate(values, { abortEarly: false })
            .then(valid => setErrors({ errorPaths: [], errors: [] })) //called if the entire form is valid
            .catch(err => {
                setErrors({ errors: err.errors, errorPaths: err.inner.map(i => i.path) })
            })
    }, [schema])

    const [createPolicy, { loading }] = useCallback(useMutation(CREATE_MOTOR_POLICY,
        {
            update(_, result) {
                context.createPolicy(result.data);
                setVisible(false);

                let pol = result.data.createMotorPolicy.motorPolicy
                context.createPolicy(result.data.createMotorPolicy.motorPolicy)
                props.history.push({
                    pathname: `/staff/dashboard/policies/general/motor/details/${pol.id}`,
                    state: { motorPolicy: pol }
                })
            },
            onError(err) {
                console.log(err.graphQLErrors && err.graphQLErrors[0] ? err.graphQLErrors[0].message : err.networkError && err.networkError.result ? err.networkError.result.errors : err)
                try {
                    if (err.graphQLErrors) {
                        setResponseErrors(err.graphQLErrors[0].message);
                    }

                    if (err.networkError !== null && err.networkError !== 'undefined') {

                        setResponseErrors(err.networkError.result.errors[0]);

                    } else if (err.graphQLErrors !== null && err.networkError !== 'undefined') {

                        setResponseErrors(err.graphQLErrors.result.errors[0]);

                    }
                } catch (e) {
                    setVisible(true);
                }

            },
            variables: values,
        }));
    const onChange = useCallback((event, { name, value }) => {
        // event.preventDefault()
        // console.log(name, value)
        setValues({ ...values, [name]: value, updated: true });
        setCalculated({ ...calculated, updated: true })
    }, [values, calculated])

    const handleOnClientSearch = (e) => {
        setSearch({ search: e.target.value });
    }
    const handleOnClientChange = (e, { value }) => {
        e.preventDefault()
        setValues({ ...values, individualClient: value, updated: true });
        // validate()
    }
    useEffect(() => {
        if (values.individualClient) {
            let client = clients.find(x => x.id === values.individualClient)
            setSelectedClient(client)
        }
    }, [clients, values.individualClient])
    const handleOnInsuranceCompChange = (e, { value }) => {
        e.preventDefault()
        setValues({ ...values, insuranceCompany: value, updated: true });
    }
    const handleOnTransactionTypesChange = (e, { value }) => {
        e.preventDefault()
        setValues({ ...values, transactionType: value, updated: true });
    }
    // const handleOnPremiumTypesChange = (e, { value }) => {
    //     e.preventDefault()
    //     if (!e.target.name) {
    //         let newValues = { ...values, premiumType: value, updated: true }
    //         setValues(newValues)
    //     }
    //     else { setValues({ ...values, [e.target.name]: e.target.value, updated: true }) }
    // }
    const handleOnInsuranceClassChange = (e, { value }) => {
        e.preventDefault()
        let newValues = { ...values, insuranceClass: value, updated: true }
        setValues(newValues)

    }
    const handleOnAddPremTypesChange = (e, { value }) => {
        e.preventDefault()
        let key = e.target.name ? e.target.id : value.split("_")[1]
        if (!e.target.name) {
            let data = { premium: value.split("_")[0] }
            if (Object.keys(addPremiums).length > 0) {
                setAddPremiums(prevAddPrem => ({
                    ...prevAddPrem,
                    [key]: { ...prevAddPrem[key], ...data }
                }));
            } else {
                let newPrem = { ...addPremiums, [key]: data }
                setAddPremiums(newPrem);
            }
        }
        else {

            if (Object.keys(addPremiums).length > 0) {
                let data = { [e.target.name]: value }
                setAddPremiums(prevAdsPrem => ({
                    ...prevAdsPrem,
                    [key]: { ...prevAdsPrem[key], ...data }
                }));
            }
            else {

                let newVal = { ...addPremiums, [key]: { ...addPremiums[key], [e.target.name]: value } }
                setAddPremiums(newVal)
            }
        }
        setValues({ ...values, updated: true });


    }

    const handleOnAddBenefitTypesChange = (e, { value }) => {
        e.preventDefault()
        let key = e.target.name ? e.target.id : value.split("_")[1]
        if (!e.target.name) {
            let data = { benefit: value.split("_")[0], amount: 0 }
            if (Object.keys(addBenefits).length > 0) {
                setAddBenefits(prevAddBen => ({
                    ...prevAddBen,
                    [key]: { ...prevAddBen[key], ...data }
                }));
            } else {
                let newBenefit = { ...addBenefits, [key ? key : 0]: data }
                setAddBenefits(newBenefit);
            }

        }
        else {
            if (Object.keys(addBenefits).length > 0) {
                let data = { [e.target.name]: value, amount: 0 }
                setAddBenefits(prevAddBen => ({
                    ...prevAddBen,
                    [key]: { ...prevAddBen[key], ...data }
                }));
            }
            else {
                let newVal = { ...addBenefits, [key]: { ...addBenefits[key], [e.target.name]: value, amount: 0 } }
                setAddBenefits(newVal)
            }
        }
        setValues({ ...values, updated: true });
        setCalculated({ ...calculated, updated: true })

    }
    const handleAddAdditionalBenefit = useCallback((e) => {
        e.preventDefault();
        if (addBenefitCount.length) {
            setAddBenefitCount([...addBenefitCount, addBenefitCount[addBenefitCount.length - 1] + 1])
        }
        else { setAddBenefitCount([1]) }
    }, [addBenefitCount])
    // const handleAddAdditionalPremium = useCallback((e) => {
    //     e.preventDefault();
    //     if (addPremiumCount.length) { setAddPremiumCount([...addPremiumCount, addPremiumCount[addPremiumCount.length - 1] + 1]) }
    //     else { setAddPremiumCount([1]) }
    // }, [addPremiumCount])
    const handleRemoveAdditionalBenefit = useCallback((event) => {
        event.preventDefault();
        const newArr = addBenefitCount.filter(e => e !== Number(event.target.id))
        setAddBenefitCount(newArr)
        if (Object.keys(addBenefits).length > 0) {
            delete addBenefits[event.target.id]
        }
        setValues({ ...values, updated: true });
        setCalculated({ ...calculated, updated: true })


    }, [addBenefitCount, addBenefits, calculated, values])
    // const handleRemoveAdditionalPremium = useCallback((event) => {
    //     event.preventDefault();
    //     const newArr = addPremiumCount.filter(e => e !== Number(event.target.id))
    //     setAddPremiumCount(newArr)
    //     if (Object.keys(addPremiums).length > 0) {
    //         delete addPremiums[event.target.id]
    //     }

    // }, [addPremiums, addPremiumCount])
    useEffect(() => {
        if (values.updated) {
            setValues({ ...values, additionalBenefits: Object.values(addBenefits), additionalPremiums: Object.values(addPremiums), updated: false })
            if (values.afterSubmit) {
                validate(values)
            }
            setOpenModal(false)
        }
    }, [values, addBenefits, addPremiums, validate])
    const onPreview = (event) => {
        event.preventDefault();
        validate(values)
        setValues({ ...values, afterSubmit: true })
        setOpenModal(true)
        if (Object.keys(values).length > 7 && !errors.errors.length) { setOpenModal(true) }
        setVisible(false);
    }

    function motorPolicyDetails() {
        return (
            <Container>
                <Grid container columns={2} divided relaxed stackable>
                    <Grid.Column>
                        <Segment><Header as='h3'>Client details</Header></Segment>
                        <Item.Group>
                            <Item>

                                <Item.Content>
                                    <Divider horizontal>Basic Information</Divider>
                                    <Item.Description>
                                        <b>Name: </b><span className='price'>{selectedClient.firstName} {selectedClient.lastName} {selectedClient.surname}</span><br />
                                        <b>Email: </b><span className='price'>{selectedClient.email}</span><br />
                                        <b>Phone Number: </b><span className='price'>{selectedClient.phoneNumber}</span><br />
                                        <b>ID Number: </b><span className='price'>{selectedClient.idNumber}</span><br />
                                        <b>kra Pin: </b><span className='price'>{selectedClient.kraPin}</span><br />
                                    </Item.Description>
                                </Item.Content>
                            </Item>

                            <Item>

                                <Item.Content>
                                    <Divider horizontal>Policy Information</Divider>
                                    <Item.Description>
                                        <b>Policy Number: </b><span className='price'>{values.policyNo}</span><br />
                                        <b>Debit Note Number: </b><span className='price'>{values.debitNoteNo}</span><br />
                                        <b>Insurance Class: </b><span className='price'>{policyOpts.insurance_class_options[values.insuranceClass]}</span><br />
                                        <b>Insurance Company: </b><span className='price'>{"values.insuranceCompany.name"}</span><br />
                                        <b>Transaction Type: </b><span className='price'>{policyOpts.transaction_type_options[values.transactionType]}</span><br />
                                        <b>Start Date: </b><span className='price'>{values.startDate}</span><br />
                                        <b>End Date: </b><span className='price'>{values.endDate}</span><br />
                                        <b>Renewal Date: </b><span className='price'>{values.renewalDate || "None"}</span><br />
                                        <b>Transaction Date: </b><span className='price'>{values.transactionDate}</span><br />
                                    </Item.Description>
                                </Item.Content>
                            </Item>
                        </Item.Group>

                    </Grid.Column>
                    <Grid.Column>
                        <Segment><Header as='h3'>Vehicle details</Header></Segment>
                        <Item.Group>
                            <Item>

                                <Item.Content>
                                    <Divider horizontal>Basic Information</Divider>
                                    <Item.Description>
                                        <b>Registration Number: </b><span className='price'>{values.registrationNo} </span><br />
                                        <b>Year of Manufacture: </b><span className='price'>{values.yearOfManufacture} </span><br />
                                        <b>Model: </b><span className='price'>{values.model} </span><br />
                                        <b>Make: </b><span className='price'>{values.make} </span><br />
                                        <b>Body: </b><span className='price'>{values.body} </span><br />
                                        <b>Color: </b><span className='price'>{values.color} </span><br />
                                        <b>CC: </b><span className='price'>{values.cc.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br />
                                        <b>Chassis Number: </b><span className='price'>{values.chasselNo} </span><br />
                                        <b>Engine Number: </b><span className='price'>{values.engineNo} </span><br />
                                        <b>Tonnage: </b><span className='price'>{values.tonnage} </span><br />
                                        <b>Value: </b><span className='price'>Ksh {values.value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br />
                                    </Item.Description>
                                </Item.Content>
                            </Item>
                        </Item.Group>
                    </Grid.Column>
                </Grid>
                {values.remarks &&
                    <Grid container columns={1} divided relaxed stackable>
                        <Grid.Column>
                            <Segment><Header as='h3'>Remarks</Header></Segment>
                            <Item.Group>
                                <Item>

                                    <Item.Content>
                                        <Item.Description>
                                            <div dangerouslySetInnerHTML={{ __html: values.remarks }} />
                                        </Item.Description>
                                    </Item.Content>
                                </Item>
                            </Item.Group>
                        </Grid.Column>
                    </Grid>}
            </Container>
        )
    }
    function premiumDetails() {
        return (
            <Container>
                <Grid container columns={values.additionalPremiums && values.additionalBenefits.length ? 3 : !!(values.additionalPremiums.length || values.additionalBenefits.length) ? 2 : 1} divided relaxed stackable>

                    <Grid.Column>
                        <Segment><Header as='h3'>Basic Premium</Header></Segment>
                        <Item.Group>
                            <Item>

                                <Item.Content>
                                    <Divider horizontal>Basic Premiums Info</Divider>
                                    <Item.Description>
                                        <b>Premium Type: </b><span className='price'>Basic Premium </span><br />
                                        <b>Commission Rate: </b><span className='price'>{values.commissionRate}% </span><br />
                                        <b>Minimum Premium Amount: </b><span className='price'>Ksh {values.minimumPremiumAmount} </span><br />
                                        <span><b>Amount: </b><span className='price'>Ksh {calculated.netPremium.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br /></span>
                                    </Item.Description>
                                </Item.Content>

                            </Item>

                        </Item.Group>
                    </Grid.Column>
                    {!!values.additionalPremiums.length && <Grid.Column>
                        <Segment><Header as='h3'>Additional Premiums</Header></Segment>
                        <Item.Group>
                            {values.additionalPremiums.map((premium, i) => (<Item.Content key={i}>
                                <Item>
                                    <Divider horizontal>.{i + 1}.</Divider>
                                    <Item.Description>
                                        <b>Premium Type: </b><span className='price'>{policyOpts.premium_type_options[addPremiums[i + 1].premium]} </span><br />
                                        {premium.commissionRate && <span><b>Commission Rate: </b><span className='price'>{premium.commissionRate + "%"}  </span><br /></span>}
                                        <span><b>Amount: </b><span className='price'>Ksh {addPremiums[i + 1] && addPremiums[i + 1].amount ? addPremiums[i + 1].amount : addPremiums[i + 1].minimumAmount ? addPremiums[i + 1].minimumAmount : "0"} </span><br /></span>
                                    </Item.Description>
                                </Item>
                            </Item.Content>))}
                        </Item.Group>
                    </Grid.Column>}
                    {(values.additionalBenefits && !!values.additionalBenefits.length) && <Grid.Column>
                        <Segment><Header as='h3'>Additional Benefits</Header></Segment>
                        <Item.Group>
                            {values.additionalBenefits.map((benefit, i) => (<Item.Content key={i}>
                                <Item>
                                    <Divider horizontal>.{i + 1}.</Divider>
                                    <Item.Description>
                                        <b>Benefit Type: </b><span className='price'>{policyOpts.additional_benefit_options[benefit.benefit]} </span><br />
                                        <b>Commission Rate: </b><span className='price'>{benefit.commissionRate ? benefit.commissionRate + "%" : ""}  </span><br />
                                        {benefit.minimumAmount && <span><b>Minimum Benefit Amount: </b><span className='price'> Ksh {benefit.minimumAmount.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br /></span>}
                                        <span><b>Amount: </b><span className='price'>Ksh {benefit.amount.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br /></span>
                                    </Item.Description>
                                </Item>
                            </Item.Content>))}
                        </Item.Group>
                    </Grid.Column>}
                </Grid>

                <div>
                    <br /><br /><Segment><Header as='h3' size="medium" textAlign="center">Policy Premium Totals</Header></Segment>
                    <Grid container columns={1} divided relaxed stackable>
                        <Grid.Column>
                            <Item.Group>
                                <Item>
                                    <Item.Content>
                                        <Item.Description>
                                            <b>Net Premium:</b><span className='price'>Ksh {calculated.netPremium.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br />
                                            <b>Gross Premium:</b><span className='price'>Ksh {calculated.grossPremium.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br /><br />
                                            <b>Gross Commission: </b><span className='price'>Ksh {calculated.grossCommission.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br />
                                            <b>Net Commission: </b><span className='price'>Ksh {calculated.netCommission.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")} </span><br />


                                        </Item.Description>
                                    </Item.Content>
                                </Item>
                            </Item.Group>
                        </Grid.Column>

                    </Grid></div>

            </Container>
        )
    }
    const panes = [
        {
            menuItem: (
                <Menu.Item key='policy'>
                    Motor Policy Details
                </Menu.Item>
            ),
            render: () => {
                return (
                    <Tab.Pane>
                        {motorPolicyDetails()}

                    </Tab.Pane>
                )
            },
        },
        {
            menuItem: (
                <Menu.Item key='premiums'>
                    Premiums Details
                </Menu.Item>
            ),
            render: () => {
                return (
                    <Tab.Pane>
                        {premiumDetails()}

                    </Tab.Pane>
                )
            },
        }
    ]


    return (
        <Container>
            <Grid container padded>
                <Grid.Column>
                    <div className="content-wrapper">
                        <Header as='h2'>
                            <Icon name='settings' />
                            <Header.Content>
                                <a href="/staff/dashboard/policies">Policy Records</a> {'>'} <a href="/staff/dashboard/policies/general">General</a> {'>'} <a href="/staff/dashboard/policies/general/motor">Motor</a> {'>'} Create New Motor Policy
                                <Header.Subheader>
                                    Fill in this form to create motor policy
                                </Header.Subheader>
                            </Header.Content>
                        </Header>
                    </div>
                </Grid.Column>
            </Grid>

            <Grid container padded>
                <Grid.Column>
                    <Form onSubmit={onPreview} noValidate className={loading ? "loading" : ''}>
                        <Form.Group>
                            <Message visible={!!errors.errors.length || visible} warning>
                                <Message.Header>Please correct the following issues:</Message.Header>
                                {!!responseErrors.length && <Message>{responseErrors}</Message>}
                                {<Message.List items={errors.errors} />}
                            </Message>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('individualClient')}>
                                <label>Client</label>
                                {clients && <Clients
                                    clients={clients}
                                    handleOnClientSearch={handleOnClientSearch}
                                    handleOnClientChange={handleOnClientChange}
                                />}
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('policyNo')}>
                                <label>Policy Number</label>
                                <Input fluid placeholder='Policy Number'
                                    name="policyNo" onChange={onChange}
                                    values={values.policyNo} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('debitNoteNo')}>
                                <label>Debit Note Number</label>
                                <Input fluid placeholder='Debit Note Number'
                                    name="debitNoteNo" onChange={onChange}
                                    values={values.debitNoteNo}
                                />
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('insuranceCompany')}>
                                <label>Insurance Company</label>
                                {insuranceCos && <InsuranceCompanies
                                    insuranceCompanies={insuranceCos}
                                    handleOnInsuranceCompChange={handleOnInsuranceCompChange}
                                />}
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('transactionType')}>
                                <label>Transaction Type</label>
                                {policyOpts && policyOpts.transaction_type_options && <TransactionTypes
                                    transactionTypes={policyOpts.transaction_type_options}
                                    handleOnTransactionTypesChange={handleOnTransactionTypesChange}
                                />}
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('insuranceClass')}>
                                <label>Insurance Class</label>
                                {policyOpts && policyOpts.insurance_class_options && <PremiumTypes
                                    premiumTypes={policyOpts.insurance_class_options}
                                    placeholder="Select Insurance Class"
                                    handleOnPremiumTypesChange={handleOnInsuranceClassChange}
                                />}
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('startDate')}>
                                <label>Start Date</label>
                                <DateInput
                                    name="startDate"
                                    placeholder="Start Date"
                                    popupPosition="bottom left"
                                    value={values.startDate ? values.startDate : ""}
                                    iconPosition="left"
                                    dateFormat="YYYY-MM-DD"
                                    onChange={onChange}
                                />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('endDate')}>
                                <label>End Date</label>
                                <DateInput
                                    name="endDate"
                                    placeholder="End Date"
                                    popupPosition="bottom left"
                                    value={values.endDate ? values.endDate : ""}
                                    dateFormat="YYYY-MM-DD"
                                    iconPosition="left"
                                    onChange={onChange}
                                />
                            </Form.Field>
                        </Form.Group>
                        <Divider horizontal>Vehicle Details</Divider>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('value')}>
                                <label>Value</label>
                                <Input fluid placeholder='Current Value'
                                    name="value" type="number" onChange={onChange}
                                    values={values.value} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('registrationNo')}>
                                <label>Registration Number</label>
                                <Input fluid placeholder='Registration Number'
                                    name="registrationNo" onChange={onChange}
                                    values={values.vehicleDetails.registrationNo} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('make')}>
                                <label>Make</label>
                                <Input fluid placeholder='Make'
                                    name="make" onChange={onChange}
                                    values={values.vehicleDetails.make} />
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('model')}>
                                <label>Model</label>
                                <Input fluid placeholder='Model'
                                    name="model" onChange={onChange}
                                    values={values.vehicleDetails.model} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('body')}>
                                <label>Body</label>
                                <Input fluid placeholder='Body'
                                    name="body" onChange={onChange}
                                    values={values.vehicleDetails.body} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('color')}>
                                <label>Color</label>
                                <Input fluid placeholder='Color'
                                    name="color" onChange={onChange}
                                    values={values.vehicleDetails.color} />
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('chasselNo')}>
                                <label>Chassis Number</label>
                                <Input fluid placeholder='Chassis Number'
                                    name="chasselNo" onChange={onChange}
                                    values={values.vehicleDetails.chasselNo} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('cc')}>
                                <label>CC</label>
                                <Input fluid placeholder='CC'
                                    name="cc" type="number" onChange={onChange}
                                    values={values.vehicleDetails.cc} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('engineNo')}>
                                <label>Engine Number</label>
                                <Input fluid placeholder='Engine Number'
                                    name="engineNo" onChange={onChange}
                                    values={values.vehicleDetails.engineNo} />
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('seatingCapacity')}>
                                <label>Seat Capacity</label>
                                <Input fluid placeholder='Seat Capacity'
                                    name="seatingCapacity" type="number" onChange={onChange}
                                    values={values.vehicleDetails.seatingCapacity} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('tonnage')}>
                                <label>Tonnage</label>
                                <Input fluid placeholder='Tonnage'
                                    name="tonnage" type="number" onChange={onChange}
                                    values={values.vehicleDetails.tonnage} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('yearOfManufacture')}>
                                <label>Year of Manufacture</label>
                                <Input fluid placeholder='Year of Manufacture'
                                    name="yearOfManufacture" type="number" onChange={onChange}
                                    values={values.vehicleDetails.yearOfManufacture} />
                            </Form.Field>
                        </Form.Group>
                        <Divider horizontal>Premiums Details</Divider>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('premiumType')}>
                                <label>Premium Type</label>
                                <Input fluid placeholder='Basic Premium'
                                    name="premiumType"
                                    value="Basic Premium" />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('minimumPremiumAmount')}>
                                <label>Minimum Premium Amount</label>
                                <Input fluid placeholder='Minimum Premium Amount'
                                    name="minimumPremiumAmount" type="number"
                                    onChange={onChange}
                                    values={values.minimumPremiumAmount} />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('commissionRate')}>
                                <label>Commission Rate (%)</label>
                                <Input fluid placeholder='Commission Rate'
                                    name="commissionRate"
                                    type="number" step="any"
                                    onChange={onChange}
                                    value={values.commissionRate} />
                            </Form.Field>
                        </Form.Group>
                        <Divider horizontal>Additional Benefits</Divider>

                        < Form.Group widths='equal'>
                            <Form.Field>
                                <Button icon floated='right' onClick={handleAddAdditionalBenefit}>
                                    <Icon name='plus square outline' />
                                </Button>
                            </Form.Field>
                        </Form.Group>
                        {addBenefitCount.map(key => (
                            < Form.Group widths='equal' key={key}>
                                <Form.Field>
                                    <label>Additional Benefit Type</label>
                                    {policyOpts && policyOpts.additional_benefit_options && <PremiumTypes
                                        premiumTypes={policyOpts.additional_benefit_options}
                                        placeholder='Select Additional Benefit Type'
                                        id={key}
                                        handleOnPremiumTypesChange={handleOnAddBenefitTypesChange}
                                    />}
                                </Form.Field>
                                <Form.Field>
                                    <label>Minimum Benefit Amount</label>
                                    <Input fluid placeholder='Minimum Additional Benefit Amount'
                                        id={key}
                                        name="minimumAmount" type="number"
                                        onChange={handleOnAddBenefitTypesChange}

                                        values={values.minimumPremiumAmount} />
                                </Form.Field>
                                <Form.Field>

                                    <Grid>
                                        <label><b>Commission Rate</b></label>
                                        <Grid.Column width={12}>
                                            <Input fluid placeholder='Commission Rate'
                                                name="commissionRate"
                                                type="number" step="any"
                                                id={key}
                                                onChange={handleOnAddBenefitTypesChange}
                                                values={values.commissionRate} />
                                        </Grid.Column>
                                        <Grid.Column width={4}>
                                            <Button id={key} key={key} icon floated='right' onClick={handleRemoveAdditionalBenefit} size="small">
                                                <Icon name='trash alternate' id={key} key={key} />
                                            </Button>
                                        </Grid.Column>
                                    </Grid>
                                </Form.Field>
                            </Form.Group>))}
                        <Divider horizontal>Additional Premiums</Divider>
                        {addPremiumCount.map(key => (<Form.Group widths={2} key={key}>
                            <Form.Field>
                                <label>Premium Type</label>
                                {policyOpts && policyOpts.premium_type_options &&
                                    <Input fluid
                                        name="premiumType"
                                        value={policyOpts.premium_type_options[addPremiums[key].premium]} />}
                            </Form.Field>
                            {addPremiums[key].commissionRate ? <Form.Field>
                                <label className="ui center aligned">Commission Rate (%)</label>
                                <Input fluid placeholder='Commission Rate'
                                    name="commissionRate"
                                    type="number" step="any"
                                    id={key}
                                    onChange={handleOnAddPremTypesChange}
                                    value={addPremiums[key] ? addPremiums[key].commissionRate : ""} />
                            </Form.Field> : <Form.Field></Form.Field>}
                            <Form.Field>
                                <label className="ui center aligned">Premium Amount</label>
                                <Grid>
                                    <Grid.Row centered width={4}>
                                        <Label size="large">{addPremiums[key] && addPremiums[key].amount ? addPremiums[key].amount : addPremiums[key].minimumAmount ? addPremiums[key].minimumAmount : "0"}</Label>
                                    </Grid.Row>
                                </Grid>
                            </Form.Field>
                        </Form.Group>))}
                        <Divider horizontal>Totals</Divider>
                        <Form.Group widths='equal'>
                            <Form.Field >
                                <label>Gross Premium</label>
                                <Label size="large">{calculated.grossPremium}</Label>
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('policyCommissionRate')}>
                                <label>Commission rate (%)</label>
                                <Input fluid placeholder='Commission Rate'
                                    name="policyCommissionRate"
                                    type="number" onChange={onChange}
                                    value={values.policyCommissionRate ? values.policyCommissionRate : ""} />
                            </Form.Field>
                            <Form.Field>
                                <label>Gross Commission</label>
                                <Label size="large">{calculated.grossCommission}</Label>

                            </Form.Field>
                            {/* </Form.Group>
                        <Form.Group widths='equal'> */}
                            <Form.Field>
                                <label>Withholding Tax</label>
                                <Label size="large">{calculated.withholdingTax}</Label>

                            </Form.Field>
                            <Form.Field>
                                <label>Net Commission</label>
                                <Label size="large">{calculated.netCommission}</Label>
                            </Form.Field>
                        </Form.Group>

                        <Divider horizontal>Remarks</Divider>
                        <CKEditor
                            editor={ClassicEditor}
                            data={values.remarks}
                            onChange={(event, editor) => {
                                const data = editor.getData();
                                setValues({ ...values, remarks: data })

                            }} />
                        <Divider horizontal>...</Divider>
                        <Button type='submit'>Preview Motor Policy</Button>

                    </Form>
                </Grid.Column>
            </Grid>
            {!!calculated.netCommission && <Modal
                dimmer={"blurring"}
                closeIcon
                open={openModal}
                onClose={() => setOpenModal(false)}>
                <Modal.Header>Motor Policy Preview</Modal.Header>
                <Modal.Content>
                    <Grid container padded>
                        <Grid.Column>
                            <Tab panes={panes} />
                        </Grid.Column>
                    </Grid>
                </Modal.Content>
                <Modal.Actions>
                    <Button positive onClick={() => {createPolicy(); setOpenModal(false)}}>
                        Save Policy
                    </Button>
                </Modal.Actions>
            </Modal>}
        </Container >
    )
}
