import React, { useState, useContext, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import {
    Grid,
    Container,
    Divider,
    Header,
    Icon,
    Segment,
    Item,
    Menu,
    Tab,
    Loader
} from 'semantic-ui-react';
import moment from 'moment';
import 'react-chat-elements/dist/main.css';
import { MessageList, Input, Button, MessageBox } from 'react-chat-elements';
import { useMutation, useQuery } from '@apollo/react-hooks';
import { ASSIGN_SALES_AGENT } from './queries'

import NestedMessages from './nestedMessages';
import { salesAgentContext } from '../../context/salesAgent';

export default function AssignSalesAgent({ props }) {
    const authContext = useContext(AuthContext);
    const [agents, setAgents] = useState([]);
    const context = useContext(salesAgentContext);
    const whatsappPhoneNumber = props.computedMatch.params.whatsappPhoneNumber
    let recipientsColumns = 1
    const [fetched, setFetched] = useState(false);
    const [pagination, setPagination] = useState({
        limit: 10,
        page: 1,
        whatsappPhoneNumber: whatsappPhoneNumber
    });
    const [values, setValues] = useState({
        whatsappPhoneNumber: whatsappPhoneNumber,
    });

    const { data } = useQuery(ASSIGN_SALES_AGENT, { variables: pagination });
    useEffect(() => {
        if (data) {
            setAgents(data.whatsappMessages);
        }
    }, [data, context, agents,]);


    const [assignSalesAgent, { loading }] = useCallback(useMutation(ASSIGN_SALES_AGENT,
        {
            update(_, result) {
                console.log(result.data);
                window.location.reload();

            },
            onError(err) {
                try {
                    console.log(err.graphQLErrors && err.graphQLErrors[0] ? err.graphQLErrors[0].message : err.networkError && err.networkError.result ? err.networkError.result.errors : err)
                } catch (e) {
                    console.log(e)
                }

            },
            variables: values,
    }));

    const handleChange = (e) => {
        e.preventDefault()
        const message = agents.items.slice(-1)[0]
        const messageId = !!message.previousResponses.length ? message.previousResponses[0].id : message.id
        setValues({ ...values, whatsappResponse: e.target.value, whatsappMessageId: messageId })
    }
    const handleSubmit = (e) => {
        e.preventDefault()
        sendMessage()
    }
    function assignment() {
        return (
            <Container>

                <Divider></Divider>
                <Input
                    placeholder="Type here..."
                    onChange={handleChange}
                    multiline={true}
                    rightButtons={
                        <Button
                            onClick={handleSubmit}
                            color='white'
                            backgroundColor='black'
                            text='Send' />
                    } />
            </Container>
        )
    }


    const panes = [
        {
            menuItem: (
                <Menu.Item key='Message'>
                    Message Details
                </Menu.Item>
            ),
            render: () => {
                return (
                    <Tab.Pane>
                        {assignment()}

                    </Tab.Pane>
                )
            },
        }
    ]

    return (
        <Container>
            <Grid container columns={1} padded>
                <Grid.Column>
                    <div className="content-wrapper">
                        <Header as='h2'>
                            <Icon name='file' />
                            <Header.Content>
                                <a href="/staff/dashboard/overview">Overview</a> {'>'} <a href="/staff/dashboard/crm">CRM</a> {'>'} <a href="/staff/dashboard/crm/agents">Messages</a> {'>'} Message Details
                                <Header.Subheader>
                                    Hey there {authContext.user.username}, find a list of motor policies under General Insurance below
                                </Header.Subheader>
                            </Header.Content>
                        </Header>
                    </div>
                </Grid.Column>
            </Grid>



            <Grid container padded>
                <Grid.Column>
                    <Tab panes={panes} />
                </Grid.Column>
            </Grid>

        </Container>
    )
}
