import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
    Grid,
    Container,
    Divider,
    Header,
    Icon,
    Segment,
    Item,
    Menu,
    Tab,
    Loader
} from 'semantic-ui-react';
import moment from 'moment';
import 'react-chat-elements/dist/main.css';
import { MessageList,Input,Button } from 'react-chat-elements';
import { useQuery } from '@apollo/react-hooks';
import { GET_WHATSAPP_MESSAGE } from './queries'
import { AuthContext } from '../../context/auth';
import { WhatsAppMessageContext } from '../../context/whatsappMessages';

export default function WhatsAppMessage({ props }) {
    const authContext = useContext(AuthContext);
    const [message, setMessage] = useState({});
    const context = useContext(WhatsAppMessageContext);
    const whatsappPhoneNumber = props.computedMatch.params.whatsappPhoneNumber
    let recipientsColumns = 1
    const { loading, data: messageData } = useQuery(GET_WHATSAPP_MESSAGE, {
        variables: { whatsappPhoneNumber: whatsappPhoneNumber }
    });
    useEffect(() => {
        if (messageData) {
            setMessage(messageData.whatsappMessage);
        }
    }, [messageData, message, context]);

    function messageDetails() {
        return (
            <Container>
                <MessageList
                className='message-list'
                lockable={true}
                toBottomHeight={'100%'}
                dataSource={[
                    {
                        position: 'right',
                        type: 'text',
                        text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit',
                        date: new Date(),
                    },
                    {
                        position: 'left',
                        type: 'text',
                        text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit',
                        date: new Date(),
                    },
                    {
                        position: 'right',
                        type: 'text',
                        text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit',
                        date: new Date(),
                    },
                ]} />
                <Divider></Divider>    
                <Input
                    placeholder="Type here..."
                    multiline={true}
                    rightButtons={
                        <Button
                            color='white'
                            backgroundColor='black'
                            text='Send'/>
                    }/>
                <Popup
                    show={this.state.show}
                    header='Lorem ipsum dolor sit amet.'
                    headerButtons={[{
                        type: 'transparent',
                        color:'black',
                        text: 'close',
                        onClick: () => {
                            this.setState({show: false})
                        }
                    }]}
                    text='Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatem animi veniam voluptas eius!'
                    footerButtons={[{
                        color:'white',
                        backgroundColor:'#ff5e3e',
                        text:"Vazgeç",
                    },{
                        color:'white',
                        backgroundColor:'lightgreen',
                        text:"Tamam",
                    }]}/>

            </Container>
        )
    }


    const panes = [
        {
            menuItem: (
                <Menu.Item key='Message'>
                    Message Details
                </Menu.Item>
            ),
            render: () => {
                return (
                    <Tab.Pane>
                        {messageDetails()}

                    </Tab.Pane>
                )
            },
        }
    ]

    return (
        <Container>
            <Grid container columns={1} padded>
                <Grid.Column>
                    <div className="content-wrapper">
                        <Header as='h2'>
                            <Icon name='file' />
                            <Header.Content>
                                <a href="/staff/dashboard/overview">Overview</a> {'>'} <a href="/staff/dashboard/crm">CRM</a> {'>'} <a href="/staff/dashboard/crm/messages">Messages</a> {'>'} Message Details
                                <Header.Subheader>
                                    Hey there {authContext.user.username}, find a list of motor policies under General Insurance below
                                </Header.Subheader>
                            </Header.Content>
                        </Header>
                    </div>
                </Grid.Column>
            </Grid>



            <Grid container padded>
                <Grid.Column>
                    <Tab panes={panes} />
                </Grid.Column>
            </Grid>

        </Container>
    )
}
