import React from 'react';
import {
    Dropdown
} from 'semantic-ui-react';

export default function Agents({handleOnAgentSearch, handleOnAgentChange, agents: data, selected, multiple=false }) {


    let allAgents = []
    data.forEach(agent => {

        allAgents.push({
            key: agent.id,
            text: agent.firstName + " " + agent.lastName + " [" + agent.email + "]",
            value: agent.id
        })
    })
    return (
        <div>
            {allAgents && <Dropdown
                placeholder='Select Agent'
                fluid
                search
                clearable
                multiple={multiple}
                selection
                required
                defaultValue={selected}
                onChange={handleOnAgentChange}
                onSearchChange={handleOnAgentSearch}
                options={allAgents}
            />}
        </div>

    )
}
