import React, { useState, useContext, useEffect, useCallback } from 'react';
import { useHistory } from 'react-router-dom';
import {
    Grid,
    Container,
    Header,
    Icon,
    Form,
    Button,
    Divider,
    Tab,
    Message,
    Menu,
    Segment,
    Radio,
} from 'semantic-ui-react';
import Messages from './listPreviousMessages';

import { AuthContext } from '../../context/auth';

import { useMutation, useQuery} from '@apollo/react-hooks';

import 'react-chat-elements/dist/main.css';


import {
   SEND_WHATSAPP_MESSAGE,GET_WHATSAPP_MESSAGES
} from './queries'
import * as yup from "yup";
import { WhatsAppMessageContext } from '../../context/whatsappMessages';

export default function SendWhatsappMessages(props) {
    let history = useHistory();
    const [errors, setErrors] = useState({
        errorPaths: [],
        errors: []
    });
    const [responseErrors, setResponseErrors] = useState([]);
    const context = useContext(WhatsAppMessageContext);
    const authContext = useContext(AuthContext);
    const [messages, setMessages] = useState();

    const [visible, setVisible] = useState(false);

    const [search, setSearch] = useState({ search: "" });
    const [values, setValues] = useState({
        messageOption: "S",
        whatsappResponse: [],
        whatsappMessageId: [],
        // whatsappPhoneNumber:"",
        page:1,
        limit:10

    });

    let schema = yup.object().shape({
        whatsappResponse: yup.string(),
        whatsappMessageId: yup.string(),

    });


    const { data: messageData } = useQuery(GET_WHATSAPP_MESSAGES, {
        variables: "+254700701209"
    });
    useEffect(() => {
        if (messageData) {
            setMessages(messageData.whatsappMessages.items);
        }
        console.log(messageData)
    }, [messageData,messages]);


    const onChange = useCallback((event, { name, value }) => {
        setValues({ ...values, [name]: value, updated: true });
    }, [values])
    const setMessageOps = useCallback(() => {
        const users = [...values.individualClients, ...values.corporateClients, ...values.contactPersons]
        if (users.length < 2) {
            setValues({ ...values, messageOption: "S", messageOptUpdated: false })
        } else {
            setValues({ ...values, messageOption: "M", messageOptUpdated: false })
        }
    }, [values])





    const validate = useCallback((values) => {
        schema.validate(values, { abortEarly: false })
            .then(valid => setErrors({ errorPaths: [], errors: [] })) //called if the entire form is valid
            .catch(err => {
                setErrors({ errors: err.errors, errorPaths: err.inner.map(i => i.path) })
            })
    }, [schema])

    const [sendMessage, { loading }] = useCallback(useMutation(SEND_WHATSAPP_MESSAGE, {
        update(_, result) {
            context.sendMessage(result.data.sendMessage.message)
            history.push({
                pathname: `/staff/dashboard/crm/whatsAppmessage/${result.data.sendWhatsappMessage.whatsappMessage.whatsappPhoneNumber}`
            })
        },
        onError(err) {
            console.log(err.graphQLErrors && err.graphQLErrors[0] ? err.graphQLErrors[0].message : err.networkError && err.networkError.result ? err.networkError.result.errors : err)
            try {
                if (err.graphQLErrors) {
                    setResponseErrors(err.graphQLErrors[0].message);
                }

                if (err.networkError !== null && err.networkError !== 'undefined') {

                    setResponseErrors(err.networkError.result.errors[0]);

                } else if (err.graphQLErrors !== null && err.networkError !== 'undefined') {

                    setResponseErrors(err.graphQLErrors.result.errors[0]);

                }
            } catch (e) {
                setVisible(true);
            }

        },
        variables: values,
    }));


    const handleOnMessageChange = (e, { value }) => {
        e.preventDefault()
        setValues({ ...values, previousResponses: value, updated: true });
    }




    const panes = [
        {
            menuItem: (
                <Menu.Item key='clients'>
                    Whatsapp Notify
                </Menu.Item>
            ),
            render: () => {
                return (
                    <Tab.Pane>
                        <Form noValidate className={loading ? "loading" : ''}>
                            <Form.Group>
                                <Message visible={!!errors.errors.length || visible} warning>
                                    <Message.Header>Please correct the following issues:</Message.Header>
                                    {!!responseErrors.length && <Message>{responseErrors}</Message>}
                                    {<Message.List items={errors.errors} />}
                                </Message>
                            </Form.Group>
                            <Divider horizontal>Send notification options</Divider>
                            <Form.Group widths='equal' as={Segment}>
                                <Form.Field>
                                    <Radio
                                        label='Send to ALL'
                                        name='radioGroup'
                                        value='all'
                                        toggle
                                        checked={values.all}
                                    />
                                </Form.Field>

                            </Form.Group>
                            <Form.Group widths='equal'>
                                <Form.Field>
                                    <label>Choose People to Send (s)</label>
                                    {messages && <Messages
                                        multiple={true}
                                        recipients={messages}
                                        handleOnMessageChange={handleOnMessageChange}
                                    />}
                                </Form.Field>
                            </Form.Group>

                            <Form.Group widths='equal'>
                                <Form.Field error={errors.errorPaths.includes('sms')}>
                                    <label>Whatsapp SMS</label>
                                    <Form.Input fluid placeholder='Send whatsapp Text'
                                        name='sms'
                                        onChange={onChange}

                                    />
                                </Form.Field>

                            </Form.Group>
                            <Button type='submit' positive onClick={() => { sendMessage(); }}>
                                Send Message
                        </Button>
                        </Form>

                    </Tab.Pane>
                )
            },
        },


    ]

    return (
        <Container>
            <Grid container padded>
                <Grid.Column>
                    <div className="content-wrapper">
                        <Header as='h2'>
                            <Icon name='settings' />
                            <Header.Content>
                                <a href="/staff/dashboard/overview">Overview</a> {'>'} <a href="/staff/dashboard/overview/crm"> CRM </a>{'>'} <a href="/staff/dashboard/crm/messages">Messages</a> {'>'} Send Messages
                                <Header.Subheader>
                                    Hello {authContext.user.username}, Fill in this form to send messages to prospective clients
                                </Header.Subheader>
                            </Header.Content>
                        </Header>
                    </div>
                </Grid.Column>
            </Grid>

            <Grid container padded>
                <Grid.Column>
                    <Tab panes={panes} />



                </Grid.Column>
            </Grid>



        </Container >
    )
}
