import { useHistory } from 'react-router-dom';
import React, { useState, useContext, useEffect, useCallback } from 'react';
import {
    Grid,
    Container,
    Header,
    Icon,
    Form,
    Message,
    Button,
    Tab
} from 'semantic-ui-react';
import moment from 'moment';
import { DateInput } from 'semantic-ui-calendar-react';
import { useMutation, useQuery } from '@apollo/react-hooks';
import { ReceiptContext } from '../../context/receipt';
import { FETCH_MOTOR_POLICIES } from '../policies/queries';
import {ADD_NEW_RECEIPT, GET_RECEIPT_OPTS } from './queries'
import * as yup from "yup";
import { AuthContext } from '../../context/auth';
import PaymentOptions from './listReceiptOptions';
import MotorPolicyList from './listMotorPolicies';


function AddNewReceipt({ props }) {

    const authContext = useContext(AuthContext);
    let history = useHistory();

    const [errors, setErrors] = useState({
        errorPaths: [],
        errors: []
    });
    const [visible, setVisible] = useState(false);

    const context = useContext(ReceiptContext);
    const [successMsg, setSuccessMsg] = useState();
    const [responseErrors, setResponseErrors] = useState([]);
    const [receiptOpts, setReceiptOpts] = useState();

    const [motorPolicies, setMotorPolicy] = useState({});
    const [motorPolicyExtras, setMotorPolicyExtras]=useState({});

    const [values, setValues] = useState({
        updated: true,
        afterSubmit: false,
        motorPolicies: []
    });

    let schema = yup.object().shape({
        receiptNumber: yup.string().required("Please provide the receipt number"),
        date: yup.date().required("Please provide the date"),
        transactionDate: yup.date().required("Please provide the transaction date"),
        amountWords: yup.string().required("Please provide the amount in words"),
        amountFigures: yup.number().required("Please provide the amount in figures"),
        issuedBy: yup.string().required("Please indicate who issued the receipt"),
        paymentMode: yup.string().required("Please indicate the payment mode used"),
        policyNumber: yup.string().required("Please provide the motor policy to receipt for"),
    });


    const { data: receiptOptsData } = useQuery(GET_RECEIPT_OPTS);
    useEffect(() => {
        if (receiptOptsData) {
            setReceiptOpts(receiptOptsData.receiptOptions);
        }
    }, [receiptOptsData, receiptOpts]);


    const { data: motorPolicyData } = useQuery(FETCH_MOTOR_POLICIES, {
        variables: motorPolicyExtras
    });
    useEffect(() => {
        if (motorPolicyData) {
            setMotorPolicy(motorPolicyData.motorPolicies)
        }
    }, [motorPolicyData, motorPolicies])

    const [addReceipt, { loading }] = useCallback(useMutation(ADD_NEW_RECEIPT, {
        update(_, result) {
            context.createReceipt(result.data);
            setVisible(false);
            let receiptData = result.data.createMotorReceipt.receipt
            history.push({
                pathname: `/staff/dashboard/receipts/profile/${receiptData.id}`,
                state: { receipt: receiptData, receiptId: receiptData.id }
            })

            setSuccessMsg('Successfully Created A New Receipt');
        },
        onError(err) {
            try {
                if (err.graphQLErrors) {
                    setResponseErrors(err.graphQLErrors[0].message);
                }

                if (err.networkError !== null && err.networkError !== 'undefined') {

                    setResponseErrors(err.networkError.result.errors[0]);

                } else if (err.graphQLErrors !== null && err.networkError !== 'undefined') {

                    setResponseErrors(err.graphQLErrors.result.errors[0]);

                }
            } catch (e) {
                setVisible(true);
            }

        },
        variables: values
    }));

    const validate = useCallback((values) => {
        schema.validate(values, { abortEarly: false })
            .then(valid => setErrors({ errorPaths: [], errors: [] })) //called if the entire form is valid
            .catch(err => {
                setErrors({ errors: err.errors, errorPaths: err.inner.map(i => i.path) })
            })
    }, [schema])


    useEffect(() => {
        if (values.updated) {
            setValues({ ...values, updated: false })
            if (values.afterSubmit) {
                validate(values)
            }
        }

    }, [values, validate])

    const onChange = useCallback((event, { name, value }) => {
        setValues({ ...values, [name]: value, updated: true });
    }, [values])

    const handleOnMotorPolicySearch = (e) => {
        setMotorPolicyExtras({ ...motorPolicyExtras, search: e.target.value });
    }
    const handleOnMotorPolicyChange = (e, { value }) => {
        e.preventDefault()
        setMotorPolicy({ ...values, motorPolicy: value, updated: true });
    }


    const handleOnReceiptOptionsChange = (e, { value }) => {
        e.preventDefault()
        setValues({ ...values, paymentMode: value, updated: true });
    }



    const handleDismiss = () => {
        setVisible(false);
        setSuccessMsg('');
    }

    const onSubmit = (event) => {
        event.preventDefault();
        setSuccessMsg('');
        validate(values)
        setValues({ ...values, afterSubmit: true })
        if (Object.keys(values).length > 7 && !errors.errors.length) { addReceipt() }
        setVisible(false);
    }
    const panes = [

        {
            menuItem: "Create Receipt",
            render: () =>
                <Grid.Column>
                    {successMsg ?
                        <Message
                            positive
                            onDismiss={handleDismiss}
                            header='status'
                            content={successMsg} /> :
                        ''}

                    <Form onSubmit={onSubmit} noValidate className={loading ? "loading" : ''}>
                        <Form.Group>
                            <Message visible={!!errors.errors.length || visible} warning>
                                <Message.Header>Please correct the following issues:</Message.Header>
                                {!!responseErrors.length && <Message>{responseErrors}</Message>}
                                {<Message.List items={errors.errors} />}
                            </Message>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('receiptNumber')}>
                                <label>Receipt Number</label>
                                <Form.Input
                                    fluid
                                    placeholder='Receipt Number'
                                    name='receiptNumber'
                                    onChange={onChange}
                                />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('issuedBy')}>
                                <label>Issued By</label>
                                <Form.Input fluid placeholder='issuedBy'
                                    name='issuedBy'
                                    onChange={onChange}
                                />
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('date')}>
                                <label>Date</label>

                                <DateInput
                                    name="date"
                                    autoComplete="off"
                                    placeholder="Date"
                                    popupPosition="bottom left"
                                    value={values.date ? values.date : ""}
                                    iconPosition="left"
                                    dateFormat="YYYY-MM-DD"
                                    maxDate={moment()}
                                    onChange={onChange}
                                />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('transactionDate')}>
                                <label>Transaction Date</label>

                                <DateInput
                                    name="transactionDate"
                                    autoComplete="off"
                                    placeholder="Transaction Date"
                                    popupPosition="bottom left"
                                    value={values.transactionDate ? values.transactionDate : ""}
                                    iconPosition="left"
                                    dateFormat="YYYY-MM-DD"
                                    maxDate={moment()}
                                    onChange={onChange}
                                />
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths='equal'>
                            <Form.Field required error={errors.errorPaths.includes('amountWords')}>
                                <label>Amount (Words)</label>
                                <Form.Input fluid placeholder='Amount In Words'
                                    name='amountWords'
                                    onChange={onChange}

                                />
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('amountFigures')}>
                                <label>Amount (Figures)</label>
                                <Form.Input fluid placeholder='Amount in Figures'
                                    name='amountFigures'
                                    onChange={onChange}
                                />
                            </Form.Field>
                        </Form.Group>
                        <Form.Group widths="equal">
                            <Form.Field required error={errors.errorPaths.includes('paymentMode')}>
                                <label>Payment Mode</label>
                                {receiptOpts && <PaymentOptions
                                    receiptOptions={receiptOpts}
                                    handleOnReceiptOptionsChange={handleOnReceiptOptionsChange}
                                />}
                            </Form.Field>
                            <Form.Field required error={errors.errorPaths.includes('policyNumber')}>
                                <label>Motor Policy</label>
                                {motorPolicies && <MotorPolicyList
                                    motorPolicies={motorPolicies}
                                    handleOnMotorPolicySearch={handleOnMotorPolicySearch}
                                    handleOnMotorPolicyChange={handleOnMotorPolicyChange}
                                />}
                            </Form.Field>
                        </Form.Group>
                        <Button type='submit'>Generate Receipt</Button>

                    </Form>
                </Grid.Column>

        }
    ]
    return (
        <Container>
            <Grid container padded>
                <Grid.Column>
                    <div className="content-wrapper">
                        <Header as='h2'>
                            <Icon name='settings' />
                            <Header.Content>
                                <a href="/staff/dashboard/client-records">Policy Records</a> {'>'} Receipt
                <Header.Subheader>
                                    Hello there {authContext.user.username}, Fill in this form to generate a receipt
                </Header.Subheader>
                            </Header.Content>
                        </Header>
                    </div>
                </Grid.Column>
            </Grid>
            <Tab className='clear-top' menu={{ secondary: true, pointing: true }} panes={panes} />
        </Container>
    )
}

export default AddNewReceipt;
