        data.forEach(receipt => {

            receiptOptions.push({
                key: policy.id,
                text: policy.policyNo,
                value: policy.id
            })
        })
    }
    return (
        <div>
            {receiptOptions && <Dropdown
                placeholder='Select Payment Mode'
                fluid
                search
                clearable
                selection
                defaultValue={selected}
                onChange={handleOnReceiptOptionsChange}
                options={receiptOptions}
            />}
        </div>

    )
}
