    if (data){
    for (const [key, value] of Object.entries(data)) {
        receiptOptions.push({
            key: key,
            text: value,
            value: key
        })
    }}
    return (
        <div>
            {allReceiptOptions && <Dropdown
                placeholder='Select Payment Mode'
                fluid
                search
                clearable
                selection
                defaultValue={selected}
                onChange={handleOnReceiptOptionsChange}
                options={allReceiptOptions}
            />}
        </div>

    )
}
