import React, { useState, useContext } from 'react';
import { Menu, Segment, Image, Dropdown } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import logo from './brooks.png';
import { AuthContext } from '../../context/auth';

export default function NavBar(props) {
  const [activeItem, setActiveItem] = useState('records');
  const context = useContext(AuthContext);

  const handleItemClick = (e, { name }) => setActiveItem(name);
  const onClickHandler = () => {
    context.logout();
  }

  const trigger = (
    <span>
      {context.user ?
        <div>
          <Image avatar src={context.user.imageUrl} /> {context.user.username}
        </div>
        :
        ''
      }
    </span>
  )

  const options = [
    { key: 'user', text: 'Account', icon: 'user', value: 'staff/profile' },
    { key: 'settings', text: 'Settings', icon: 'settings', value: 'staff/settings' },
    { key: 'sign-out', text: 'Sign Out', icon: 'sign out', value: 'logout' },
  ]

  const handleDropdown = (event, data) => {
    switch (data.value) {
      case 'logout':
        onClickHandler();
        break
      default:
        return data.value
    }
  }

  return (
    <Segment>

      <Menu secondary>
        <Menu.Item position='left'>
          <Image src={logo} size='small' href='/' />

        </Menu.Item>
        {context.user && ((context.user.roles && context.user.roles.includes("admin")) || (context.user.tokenAuth && context.user.tokenAuth.user.roles.includes("admin"))) && <Menu.Item
          name='dashboard'
          active={activeItem === 'dashboard'}
          onClick={handleItemClick}
          as={Link}
          to="/admin/dashboard"
        />}
        {context.user && <Menu.Item
          name='records'
          active={activeItem === 'records'}
          onClick={handleItemClick}
          as={Link}
          to="/staff/dashboard/overview"
        />}
        {context.user && <Menu.Item
          name='billing'
          active={activeItem === 'billing'}
          onClick={handleItemClick}
        />}
        {context.user && <Menu.Item
          name='reports'
          active={activeItem === 'reports'}
          onClick={handleItemClick}
          position='left'
        />}
        {context.user && <Menu.Item position='right'>
          {window.location.pathname !== "/" || context.user ? <Dropdown
            trigger={trigger}
            options={options}
            pointing='top right'
            icon={null}
            onChange={handleDropdown}
          /> : ""}
        </Menu.Item>
        }
      </Menu>
    </Segment>
  )
}
